package com.sampreeth.helloworld;

public class arth_oper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int j=2;
		System.out.println(" + is  "+ (i+j));
		System.out.println("- "+ (i-j));
		System.out.println("* "+i*j);
		System.out.println("/ "+ i/j);
		System.out.println("%"+ i % j);
	}
}