package com.sampreeth.helloworld;

import java.util.*;

public class number {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int a=s.nextInt();
		if(a<0) {
			System.out.println(a+"is negative");
		}
		else {
			System.out.println(a+" is positive");
		}
	}

}
